import React from 'react'
import { Pie } from 'react-chartjs-2';
import {Chart as ChartJS,ArcElement,Tooltip,Legend} from 'chart.js';

ChartJS.register(ArcElement,Tooltip,Legend)

const Piechart = ({data,selectedOption}) => {
    const chartData = {
        labels: data?.map(item => Object.keys(item)[0]), // Extract labels from the keys
        datasets: [
          {
            data: data?.map(item => Object.values(item)[0]), // Extract values
            backgroundColor: [
              'rgba(255, 99, 132, 0.6)',
              'rgba(54, 162, 235, 0.6)',
              'rgba(255, 206, 86, 0.6)',
              'rgba(75, 192, 192, 0.6)',
              'rgba(255, 99, 50, 0.6)',

            ],
          },
        ],
    };
  return (
    <>
    <div className='col-10 offset-1 col-lg-6 offset-lg-3 col-xl-4 offset-xl-4 text-center' style={{padding:"20px"}}>
    <p style={{fontWeight:"bold",fontSize:"26px",color:"red"}} className='mb-5 mb-lg-2'>Pie Chart Stats-{selectedOption}</p>
    <Pie data={chartData}/>
    </div>
    </>

  )
}

export default Piechart
