import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App = () => {
  const [transactions, setTransactions] = useState([]);
  const [statistics, setStatistics] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Initialize the database
    axios.get('http://localhost:3001/api/initialize-database')
      .then(response => console.log(response.data))
      .catch(error => console.error(error));

    // Fetch all transactions
    axios.get('http://localhost:3001/api/transactions')
      .then(response => {
        setTransactions(response.data.data);
        setLoading(false);
      })
      .catch(error => console.error(error));

    // Fetch statistics for a specific month (e.g., 'January')
    axios.get('http://localhost:3001/api/statistics/January')
      .then(response => {
        setStatistics(response.data.statistics);
        setLoading(false);
      })
      .catch(error => console.error(error));
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>Transactions</h2>
      <ul>
        {transactions.map(transaction => (
          <li key={transaction.id}>{transaction.title} - ${transaction.price}</li>
        ))}
      </ul>

      <h2>Statistics</h2>
      <p>Total Sale Amount: ${statistics.totalSaleAmount}</p>
      <p>Total Sold Items: {statistics.totalSoldItems}</p>
      <p>Total Not Sold Items: {statistics.totalNotSoldItems}</p>
    </div>
  );
};

export default App;
