import { useState, useEffect } from "react";
import axios from "axios";
import { API } from "../../global";
import "./index.css";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  responsive: true,
  plugins: {
    legend: {
      position: "top",
    },
    title: {
      display: true,
    },
  },
};

export function BarChart() {
  const [barChartData, setBarChartData] = useState({});
  const [data, setData] = useState([]);
  const [lab, setLab] = useState([]);
  const [month, setMonth] = useState("March");

  useEffect(() => {
    async function fetchBarChartData() {
      try {
        const response = await axios.get(`${API}/bar-chart/${month}`);
        console.log(response.data.result);

        // Set state variables directly from the response
        setBarChartData(response.data.result);
        setLab(Object.keys(response.data.result));
        setData(Object.values(response.data.result));

        console.log(lab);
        console.log(barChartData);
      } catch (error) {
        console.error("Error fetching bar chart data", error);
      }
    }

    fetchBarChartData();
  }, [month]);

  return (
    <div>
      <div className="cardgraphcontainer">
      <h1>
        Bar Chart Stats -{" "}
        {month.displayText === "Select Month" ? "Overall" : month.displayText}
      </h1>
      </div>
      <h1>
        Bar Cart Stats-<span className="month">{month}</span>
      </h1>
      
      <div className="bar">
      <Bar 
        options={options}
        data={{
          labels: lab,
          datasets: [
            {
              label: "Number of Items",
              data: data,
              backgroundColor: "lightblue",
            },
          ],
        }}
      />
      </div>
    </div>
  );
}

export default BarChart;
